/*
** ===================================================================
**     Method      :  I2cSlave_Interrupt (component I2C_LDD)
**
**     Description :
**         The method services the interrupt of the selected peripheral(s)
**         and eventually invokes event(s) of the component.
**         This method is internal. It is used by Processor Expert only.
** ===================================================================
*/
#define ON_SLAVE_BLOCK_SENT     0x01U
#define ON_SLAVE_BLOCK_RECEIVED 0x02U

PE_ISR(I2cSlave_Interrupt)
{
  /* {Default RTOS Adapter} ISR parameter is passed through the global variable */
  I2cSlave_TDeviceDataPtr DeviceDataPrv = INT_I2C0__DEFAULT_RTOS_ISRPARAM;
  uint8_t Flags = 0x00U;               /* Temporary variable for flags */
  LDD_I2C_TErrorMask ErrorMask = 0x00U; /* Temporary variable for error mask */
  register uint8_t Status;             /* Temporary variable for status register */

  Status = I2C_PDD_ReadStatusReg(I2C0_BASE_PTR); /* Safe status register */
  I2C_PDD_ClearInterruptFlags(I2C0_BASE_PTR, (Status)); /* Clear interrupt flag */
  if (I2C_PDD_GetMasterMode(I2C0_BASE_PTR) == I2C_PDD_MASTER_MODE) { /* Is device in master mode? */
  } else {
    if ((Status & I2C_PDD_ADDRESSED_AS_SLAVE) != 0x00U) { /* Addressed as a slave? */
      if ((Status & I2C_PDD_ARBIT_LOST) != 0x00U) { /* Arbitration lost? */
        ErrorMask |= LDD_I2C_ARBIT_LOST; /* Set the ArbitLost error mask */
      }
      if ((Status & I2C_PDD_SLAVE_TRANSMIT) != 0x00U) { /* Read? */
        I2C_PDD_SetTransmitMode(I2C0_BASE_PTR, I2C_PDD_TX_DIRECTION); /* Switch to Tx mode */
        if (DeviceDataPrv->OutLenS < DeviceDataPrv->OutLenSReq) { /* Is the requested number of characters transmitted? */
          DeviceDataPrv->OutLenS++;    /* Increment number of chars in the transmit buffer */
          I2C_PDD_WriteDataReg(I2C0_BASE_PTR, *(DeviceDataPrv->OutPtrS)++); /* Store char to transmitter register */
          if (DeviceDataPrv->OutLenS == DeviceDataPrv->OutLenSReq) { /* Is last char. transmitted? */
            Flags |= ON_SLAVE_BLOCK_SENT; /* Set OnSlaveBlockSent flag if last character was sent */
            DeviceDataPrv->OutLenSReq = 0x00U; /* Slave block is transmitted */
          }
        } else {
          I2C_PDD_WriteDataReg(I2C0_BASE_PTR, I2cSlave_EMPTY_CHAR); /* Store the empty char to the transmit register */
          ErrorMask |= LDD_I2C_SLAVE_TX_UNDERRUN; /* Set the SlaveTxUnderrun error mask */
        }
      } else {
        I2C_PDD_SetTransmitMode(I2C0_BASE_PTR, I2C_PDD_RX_DIRECTION); /* Switch to Rx mode */
        (void)I2C_PDD_ReadDataReg(I2C0_BASE_PTR); /* Dummy read character */
      }
    } else {
      if ((Status & I2C_PDD_ARBIT_LOST) != 0x00U) { /* Arbitration lost? */
        ErrorMask |= LDD_I2C_ARBIT_LOST; /* Set the ArbitLost error mask */
      } else {
        if (I2C_PDD_GetTransmitMode(I2C0_BASE_PTR) == I2C_PDD_TX_DIRECTION) { /* Transmit? */
          if ((Status & I2C_PDD_RX_ACKNOWLEDGE) != 0x00U) { /* NACK from receiver */
            I2C_PDD_SetTransmitMode(I2C0_BASE_PTR, I2C_PDD_RX_DIRECTION); /* Switch to Rx mode */
            (void)I2C_PDD_ReadDataReg(I2C0_BASE_PTR); /* Dummy read character */
          } else {
            if (DeviceDataPrv->OutLenS < DeviceDataPrv->OutLenSReq) { /* Is the requested number of characters transmitted? */
              DeviceDataPrv->OutLenS++; /* Decrease number of chars in the transmit buffer */
              I2C_PDD_WriteDataReg(I2C0_BASE_PTR, *(DeviceDataPrv->OutPtrS)++); /* Store char to transmitter register */
              if (DeviceDataPrv->OutLenS == DeviceDataPrv->OutLenSReq) { /* Is last char. transmitted? */
                Flags |= ON_SLAVE_BLOCK_SENT; /* Set OnSlaveBlockSent flag if last character was sent */
                DeviceDataPrv->OutLenSReq = 0x00U; /* Slave block is transmitted */
              }
            } else {
              I2C_PDD_WriteDataReg(I2C0_BASE_PTR, I2cSlave_EMPTY_CHAR); /* Store the empty char to the transmit register */
              ErrorMask |= LDD_I2C_SLAVE_TX_UNDERRUN; /* Set the SlaveTxUnderrun error mask */
            }
          }
        } else {
          if (DeviceDataPrv->InpLenS < DeviceDataPrv->InpLenSReq) { /* Is the requested number of characters received? */
            DeviceDataPrv->InpLenS++;  /* Increment number of chars in the receive buffer */
            *(DeviceDataPrv->InpPtrS)++ = I2C_PDD_ReadDataReg(I2C0_BASE_PTR); /* Save received char to the receive buffer */
            if (DeviceDataPrv->InpLenS == DeviceDataPrv->InpLenSReq) { /* Is number of bytes in the receive buffer equal to the size of buffer? */
              Flags |= ON_SLAVE_BLOCK_RECEIVED; /* Set OnSlaveBlockReceived flag */
              DeviceDataPrv->InpLenSReq = 0x00U; /* Slave block is received */
            }
          } else {
            (void)I2C_PDD_ReadDataReg(I2C0_BASE_PTR); /* Dummy read data register */
            ErrorMask |= LDD_I2C_SLAVE_RX_OVERRUN; /* Set the SlaveRxOverrun error mask */
          }
        }
      }
    }
    if ((Flags & ON_SLAVE_BLOCK_SENT) != 0x00U) { /* Is OnSlaveBlockSent flag set? */
      I2cSlave_OnSlaveBlockSent(DeviceDataPrv->UserData); /* If yes then invoke user event */
    }
    if ((Flags & ON_SLAVE_BLOCK_RECEIVED) != 0x00U) { /* Is OnSlaveBlockReceived flag set? */
      I2cSlave_OnSlaveBlockReceived(DeviceDataPrv->UserData); /* If yes then invoke user event */
    }
    if (ErrorMask != 0x00U) {          /* Is any error mask set? */
      DeviceDataPrv->ErrorMask |= ErrorMask; /* Update list of error mask value */
      I2cSlave_OnError(DeviceDataPrv->UserData); /* If yes then invoke user event */
    }
  }
}

